    public boolean showIndentCell() {
        int oldCnt;
        Number oldCount = (Number)_indexDict.objectForKey(new Integer(vheadingIndex()));

        if (oldCount == null) {
            oldCnt = 0;
        } else {
            int span = this.model().spanForVListIndexAsCellsShowHeadingShowTotals(true, vheadingIndex(), !this.showRecordGroupAsTable(), this.showRecordGroupHeadings(), this.selectedRecordGroupDisplayType().equals("TOTALS"));
            oldCnt = oldCount.intValue();
            oldCnt++;

            if (oldCnt > (span-1)) {
                oldCnt = 0;
            }

        }

        _indexDict.setObjectForKey(new Integer(oldCnt), new Integer(vheadingIndex()));

        if (oldCnt == 0) {
            return true;
        }

        return false;
    }


